import React from 'react';

const moviesNowShowing = [
  {
    id: 1,
    title: 'Doctor Strange',
    duration: '2h 6m',
    rating: 5,
    poster: 'https://link-to-doctor-strange-poster.jpg',
    censors: 'PG-13',
  },
  {
    id: 2,
    title: 'The Avengers',
    duration: '3h 2m',
    rating: 4,
    poster: 'https://link-to-the-avengers-poster.jpg',
    censors: 'PG-13',
  },
  {
    id: 3,
    title: 'Thor',
    duration: '1h 54m',
    rating: 4,
    poster: 'https://link-to-thor-poster.jpg',
    censors: 'PG-13',
  },
  {
    id: 4,
    title: 'Captain Marvel',
    duration: '2h 4m',
    rating: 4,
    poster: 'https://link-to-captain-marvel-poster.jpg',
    censors: 'PG-13',
  },
];

const moviesComingSoon = [
  {
    id: 5,
    title: 'Black Panther',
    duration: '2h 15m',
    poster: 'https://link-to-black-panther-poster.jpg',
    censors: 'PG-13',
  },
  {
    id: 6,
    title: 'Captain America',
    duration: '2h 10m',
    poster: 'https://link-to-captain-america-poster.jpg',
    censors: 'PG-13',
  },
  {
    id: 7,
    title: 'Ant-Man',
    duration: '1h 57m',
    poster: 'https://link-to-ant-man-poster.jpg',
    censors: 'PG-13',
  },
  {
    id: 8,
    title: 'Iron Man 3',
    duration: '2h 10m',
    poster: 'https://link-to-iron-man-3-poster.jpg',
    censors: 'PG-13',
  },
];

const MovieSection = ({ title, movies, buttonText }) => {
  return (
    <div className="my-10">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold text-white">{title}</h2>
        <a href="#" className="text-blue-400 hover:underline">View all</a>
      </div>
      <div className="grid grid-cols-4 gap-6">
        {movies.map((movie) => (
          <div key={movie.id} className="bg-gray-800 p-4 rounded-lg">
            <img src={movie.poster} alt={movie.title} className="w-full h-[250px] object-cover rounded-md mb-4" />
            <div className="flex justify-between items-center mb-2">
              <span className="text-sm text-gray-400">{movie.duration}</span>
              <span className="flex space-x-1">
                {Array(movie.rating).fill(0).map((_, i) => (
                  <span key={i} className="text-yellow-400">★</span>
                ))}
                {Array(5 - movie.rating).fill(0).map((_, i) => (
                  <span key={i} className="text-gray-500">★</span>
                ))}
              </span>
            </div>
            <h3 className="text-xl font-bold text-white mb-2">{movie.title}</h3>
            <span className="text-sm text-gray-400 mb-2 block">{movie.censors}</span>
            <button className="bg-yellow-500 text-black px-4 py-2 rounded-md hover:bg-yellow-600 transition">{buttonText}</button>
          </div>
        ))}
      </div>
    </div>
  );
};

const BodyContent = () => {
  return (
    <div className="container mx-auto px-10">
      <MovieSection title="Now Showing" movies={moviesNowShowing} buttonText="Book Tickets" />
      <MovieSection title="Coming Soon" movies={moviesComingSoon} buttonText="Watch Trailer" />
    </div>
  );
};

export default MovieSection;
